    // Speed-up timers (https://stackoverflow.com/a/76950050)
    // Save the original versions of setTimeout and setInterval
    function speedUp() {
        const originalSetTimeout = window.setTimeout;
        const originalSetInterval = window.setInterval;

        // Replace window.setTimeout with custom version
        window.setTimeout = function (callback, delay, ...args) {
            const adjustedDelay = (delay || 0) / 100;
            return originalSetTimeout(callback, adjustedDelay, ...args);
        };

        // Replace window.setInterval with custom version
        window.setInterval = function (callback, delay, ...args) {
            const adjustedDelay = (delay || 0) / 100;
            return originalSetInterval(callback, adjustedDelay, ...args);
        };
    }

